(function($){$.opiSimpleSlideshow=function(el,settings){var slider=this;slider.defaults={auto:true,setPosition:true,speed:100,interval:5000,zIndex:10,pauseOnHover:false};slider.init=function(){slider.options=$.extend({},slider.defaults,settings);slider.$el=$(el);slider.$el.children().css('zIndex',slider.options.zIndex);if(slider.options.setPosition)
slider.$el.css('position','relative').children().css('position','absolute');if(!$('.current',slider.$el).length)$(':first',slider.$el).addClass('current').css('zIndex',slider.options.zIndex+2);if(slider.options.pauseOnHover){slider.$el.hover(function(){slider.pause();},function(){slider.play();});}
if(slider.options.auto)slider.play();};slider.move=function(){slider.current=$('.current',slider.$el).addClass('last-current').css('zIndex',slider.options.zIndex+1);slider.next=slider.current.next().length?slider.current.next():$(':first',slider.$el);slider.next.css({opacity:0.0}).addClass('current').css('zIndex',slider.options.zIndex+2).animate({opacity:1.0},slider.options.speed,function(){slider.current.removeClass('current last-current').css('zIndex',slider.options.zIndex);});};slider.pause=function(){if(slider.interval)window.clearInterval(slider.interval);}
slider.play=function(){slider.pause();slider.interval=window.setInterval(function(){slider.move();},slider.options.interval);};slider.init();};$.fn.opiSimpleSlideshow=function(settings){return this.each(function(i){(new $.opiSimpleSlideshow(this,settings));});};})(jQuery);
