(function($){$.opiSimpleSlideshow=function(el,settings){var slider=this;slider.init=function(){slider.options=$.extend({},$.opiSimpleSlideshow.defaults,settings);slider.el=$(el);slider.el.children().css({'zIndex':slider.options.zIndex});if(slider.options.setPosition)slider.setPosition();if(!$('.current',slider.el).length)$(':first',slider.el).addClass('current').css('zIndex',slider.options.zIndex+2);if(slider.options.auto){slider.interval=window.setInterval(function(){slider.move();},slider.options.interval);}}
slider.move=function(){slider.current=$('.current',slider.el).css('zIndex',slider.options.zIndex+2);slider.next=slider.current.next().length?slider.current.next():$(':first',slider.el);slider.current.addClass('last-current').css('zIndex',slider.options.zIndex+1);slider.next.css({opacity:0.0}).addClass('current').css('zIndex',slider.options.zIndex+2).animate({opacity:1.0},slider.options.speed,function(){slider.current.removeClass('current last-current').css('zIndex',slider.options.zIndex);});}
slider.setPosition=function(){slider.el.css('position','relative');slider.el.children().css({'position':'absolute'});}
slider.init();};$.opiSimpleSlideshow.defaults={auto:true,setPosition:true,speed:1000,interval:3000,zIndex:10};$.fn.opiSimpleSlideshow=function(settings){return this.each(function(i){(new $.opiSimpleSlideshow(this,settings));});};})(jQuery);
